import React, {useState} from "react";
import {Link, useNavigate} from "react-router-dom";
import "../css/Home.css";

function Home() {
  const [postalCode, setPostalCode] = useState("")
  const navigate = useNavigate()

  const gallery1 = [
      "old-fence.png",
      "white-fence.png"
  ]

  const handleChange = (event) => {
    setPostalCode(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate('/book-now')
  };

  const [currentImage, setCurrentImage] = useState(0);

  const nextImage = () => {
    setCurrentImage((prevImage) => (prevImage + 1) % gallery1.length);
  };

  const previousImage = () => {
    setCurrentImage((prevImage) =>
      prevImage === 0 ? gallery1.length - 1 : prevImage - 1
    );
  };

  return (
    <div className="home">
      <div className="header">
        <h1> Your Partner in Fence Restoration </h1>
      </div>
      <div className="consultation">
        <Link to="/services"> All Services </Link>
        <Link to="/services/packages"> Packages </Link>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={postalCode}
            onChange={handleChange}
            placeholder="Postal Code"
          />
          <button type="submit"> Book A Consultation </button>
        </form>
      </div>
      <div className="reviews">
        <div className="reviews-slot">
          <div className="image-gallery">
            <button className="arrow left-arrow" onClick={previousImage}>
              &#8249;
            </button>
            <img src={imagesSlot1[currentImage]} alt={`Image ${currentImage}`} />
            <button className="arrow right-arrow" onClick={nextImage}>
              &#8250;
            </button>
          </div>
          <div className="quote">
            <p>quote 1</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
