import React, { useState } from "react"
import { Link, useNavigate } from "react-router-dom";
import ImageGallery from "../components/ImageGallery"
import OldFence from "../assets/old-fence.png"
import WhiteFence from "../assets/white-fence.png"
import "../css/Home.css"

function Home() {
  const [postalCode, setPostalCode] = useState("")
  const navigate = useNavigate()

  const gallery1 = [
    OldFence,
    WhiteFence
  ]

  const handleChange = (event) => {
    setPostalCode(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    navigate('/book-now')
  }

  return (
    <div className="home">
      <div className="header">
        <h1> Your Partner in Fence Restoration </h1>
      </div>
      <div className="consultation">
        <Link to="/services"> All Services </Link>
        <Link to="/services/packages"> Packages </Link>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={postalCode}
            onChange={handleChange}
            placeholder="Postal Code"
          />
          <button type="submit"> Book A Consultation </button>
        </form>
      </div>
      <div className="reviews">
        <ImageGallery images={gallery1} />
        <p>"Look how they renewed my fence!" - Karen</p>
        <ImageGallery images={gallery1} />
        <p>"They did such a great job! I would definitely recommend" - Sharon</p>
        <ImageGallery images={gallery1} />
        <p>"These guys are great! Nice and kind gentlemen they are!" - Bill</p>
        <ImageGallery images={gallery1} />
        <p>"I didn't even know my fences needed to be taken care of. Thanks to Wing and Hieu my fences look brand new!" - Paul</p>
      </div>
    </div>
  );
}

export default Home;
